import { StatusBar } from 'expo-status-bar';

import React ,{ useState, useEffect }from 'react';

import Component from 'react';

import {TouchableOpacity, Text, View, FlatList, Button } from 'react-native';

import styles from './styles/Gridstyles';

const map=[];

var OpenSet=[];
var ClosedSet=[];

var xSize = 5;
var ySize = 5;
var mapSize = xSize*ySize;

var startStop = [];

// var start = [0,0];
// var end = [xSize-1,ySize-1];

var start = 0;
var end = 0;

var startID = 0;
var endID = 0;

var last;

var UltimateCurrent = [];

var goal = false;


var testi;

export default class app extends React.Component{

  constructor(props) {
    super(props);
    this.state={movecount:0};
    this.handleUpdate = this.handleUpdate.bind(this);
    this.navigate = this.navigate.bind(this);

    Mapinit(); 
  }

  handleUpdate = () =>{
    UltimateCurrent.push(map[startID]);
    if (goal == true){
      map.splice(0,map.length);
      startStop.splice(0,startStop.length);
      Mapinit();
      UltimateCurrent.push(map[startID]);
      this.setState({movecount :this.state.movecount - this.state.movecount}) 
    }

    aStar(map[startID]);
    //trail();
    console.log(map);
    this.setState({movecount :this.state.movecount + 1})    
  }

  navigate = (id) =>{
    if(startStop.length < 2){
      startStop.push(map[id]);
      console.log(startStop);
    }
    else{
      console.log('On jo kaksi');
    }

    if(startStop.length > 1){
      end = startStop[1].Location;
      endID = startStop[1].ID;
    }
    else{
      start = startStop[0].Location;
      startID = startStop[0].ID;
    }

    console.log(startID);
    console.log(endID);
  }

  render(){
    return(
    <View style={styles.container}>
      <Text>{this.state.movecount}</Text>
      <Button title="try" onPress={this.handleUpdate} style={styles.startbutton}></Button>
      <FlatList
        data={map}
        extraData={this.state.movecount}
        renderItem={({item}) => <View style={drawmap(item)}>
          <TouchableOpacity style={styles.button} onPress={()=>{this.navigate(item.ID)}}>
            <Text style={styles.GridViewInsideTextItemStyle}>{item.fscore}</Text>
          </TouchableOpacity>            
        </View>}
        numColumns={xSize}
      />
    </View>
    );
  } 
}


function aStar(current){
  var CurrentID = current.ID;
  var GScore = 0; // jo kuljettu matka
  var FScore; // On G + arviotu matka

  OpenSet.push(CurrentID);
  do{
    var mingscore = 100; 

    for (let index = 0; index < OpenSet.length; index++) {
      if(map[OpenSet[index]].gscore < mingscore){
        mingscore = map[OpenSet[index]].gscore; // mikä on pienin fscore opensetissä
      }
    }

    console.log(OpenSet);

    for (let index = 0; index < OpenSet.length; index++) {
      if(map[OpenSet[index]].gscore > mingscore){ // poistetaan kaikki patsi ne joilla on pienin fscore
        ClosedSet.push(OpenSet[index]);
        removeFromOpenList(OpenSet[index]);
      }
    }

    console.log(OpenSet);

    var possibilities = OpenSet.length;

    for (let index = 0; index < possibilities; index++) {
      CurrentID = OpenSet[index];
      ClosedSet.push(CurrentID);
      removeFromOpenList(CurrentID);
        map[CurrentID].Neighbours = Neighbours(map[CurrentID].x, map[CurrentID].y, map[CurrentID].ID); // katsotaan naapurit ja  tallennetaan niiden ID listaan objektin sisään
        for (let index = 0; index < map[CurrentID].Neighbours.length; index++) {
          var NeighbourID = map[CurrentID].Neighbours[index].ID;
          map[NeighbourID].gscore = GScore; // jo kuljettu matka
          map[NeighbourID].fscore = GScore + Distance(map[NeighbourID].Location);
          if(InOpenSet(NeighbourID) == false && InClosedSet(NeighbourID) == false){
            OpenSet.push(NeighbourID);
            console.log('tämän lisäsin' + NeighbourID);
          }
        }

    }

    console.log(CurrentID);
    console.log(OpenSet);
    console.log(ClosedSet);

    GScore = GScore + 1;
  }while(CurrentID != endID)//(JSON.stringify(current.Location)!=JSON.stringify(end))
}

function removeFromOpenList(removeID){
  for (let index = 0; index < OpenSet.length; index++) {
    if(OpenSet[index] == removeID){
      OpenSet.splice(index,1);
    }
  }
}

function InClosedSet(id){
  var is = false;
  for (let index = 0; index < ClosedSet.length; index++) {
    if(id == ClosedSet[index]){
      is  = true;
    }
  }

  console.log(is);
  return is;
}

function InOpenSet(id){
  var is = false;
  for (let index = 0; index < OpenSet.length; index++) {
    if(id == OpenSet[index]){
      is  = true;
    }
  }
  console.log(is);
  console.log(id);
  return is;
}


function trail(){

  var trailPath = map[last];
  trailPath.trail = true;
  var past = true;

  map[startID].trail = true; // värjää aloituksen reitiksi

  do{
    var bestFscore = mapSize
    var bestID = 0;

    for (let index = 0; index < trailPath.Neighbours.length; index++) {

    }

    last = map[trailPath.ID];
    trailPath = map[bestID];

    console.log(trailPath.ID);

    if(trailPath.ID==startID){
      past = false;
    }

  }while(past != false)
}

function DoesInclude(id){
  var already = false;
  for (let index = 0; index < OpenSet.length; index++) {
    if(OpenSet[index].ID == id){
      already = true;
      console.log("tarkistetaan on duplicatee");
      console.log(OpenSet[index].ID);
      console.log(id);
    }
  }
  return already;
}

function cell(nx,ny,random,idnum){

  let ID = idnum;
  let cameFrom = 0;
  let x = nx;
  let y = ny;
  let loc = [x,y];
  let fscore = 0;
  let gscore = 0;
  let wall = wallorNot(random, loc);
  var blocks = [];
  var trail = false;

  // jatka tästä
  return {'x':x,'y':y,'wall':wall, 'Neighbours':blocks, 'Location':loc , 'fscore':fscore, 'gscore':gscore ,'ID':ID, 'cameFrom':cameFrom,'trail':trail};
}

function Neighbours(x, y, id){

  var NL = [];

  if(y > 0){ // voiko mennä ylös

    if (map[id-(xSize)].wall != true){
      NL.push(map[id-(xSize)]);
    }
  }
  if(y < ySize-1){ // voiko mennä alas

    if (map[id+(xSize)].wall != true){
      NL.push(map[id+(xSize)]);
    }
  }
  if(x > 0){ // voiko mennä vasemmalle

    if (map[id-1].wall != true){
      NL.push(map[id-1]);
    }
  }
  if(x < xSize-1){ // voiko mennä oikealle

    if (map[id+1].wall != true){
      NL.push(map[id+1]);
    }
  }

  for (let x = 0; x < OpenSet.length; x++) {
    for (let y = 0; y < NL.length; y++) {
      if (OpenSet[x].ID == NL[y].ID){
        NL.splice(y,1);
      }

    }
  }

  return NL;
}

function wallorNot(random,loc){
  if(JSON.stringify(loc)==JSON.stringify(start) || JSON.stringify(loc) == JSON.stringify(end)){
    return false;
  }
  else{
    if(random>0.8){
      return true;
    }
    else{
      return false;
    }
  }
}

function Mapinit(){
  var num = 0;
  for (let y = 0; y < ySize; y++) {
    for (let x = 0; x < xSize; x++) {
      var randomnum = Math.random();
      map.push(cell(x,y,randomnum,num));
      num++
     }
  }
}

function drawmap(item){
  var blockstyle;
  if (item.trail == true){
    blockstyle = styles.Trail;
  }
  if (!item.wall){
    if (item.trail == true){
      blockstyle = styles.Trail;
    }
    else{
    blockstyle = styles.mapfree;
    }
  }
  else{
    blockstyle = styles.mapblock;
  }
  return(blockstyle);
}

function Distance(location){
  return Math.abs(location[0]-end[0])+Math.abs(location[1]-end[1]); // toimii vain jos aloitus on vasemmassa yläkulmassa
}
