import { StatusBar } from 'expo-status-bar';

import React ,{ useState, useEffect }from 'react';

import Component from 'react';

import { DrawerLayoutAndroidComponent, StyleSheet, Text, View, FlatList, Button } from 'react-native';

  const blocks=[];
  const moveValues=[];

  var xSize = 5;
  var ySize = 5;
  var mapSize = xSize*ySize;
  const dir = [];
  var movecount = 0;

  export default function app (){

  const [newMap, setnewMap]=useState([]);

  setnewMap(newMap=>[...newMap, {Mapinit}])

  return(
    <View style={styles.container}>
      <Text>LOL tää toimii</Text>
      <Button onClick={testmove} style={styles.startbutton}>Start</Button>
      <FlatList
        data={newMap}
        extraData={movecount}
        renderItem={({item}) => <View style={drawmap(item)}>
          <Text style={styles.GridViewInsideTextItemStyle}>{item.numv}</Text>            
        </View>}
        numColumns={5}
      />
    </View>
  );
}

function testmove(){
  var loc = 1;
  newMap[loc] = {numv:loc, func:'worm'};
  movecount=movecount+1
}

function DistanceToEnd(loc){
  var distance;
  switch (loc) {
    case loc<6:
      distance = (loc - 5)+4;
      break;
    case loc<11:
      distance = (loc - 10)+3;
      break;
    case loc<16:
      distance = (loc - 15)+2;
      break;
    case loc<21:
      distance = (loc - 20)+1;
      break;
    case loc<26:
      distance = (loc - 25)+0;
      break;
  }

  return(
    distance
  )
}

function bestmove(loc){
  moves(loc)

  for (let index = 0; index < dir.length; index++) {
    var value = 25 - DistanceToEnd(dir[index]);
    if (blocks.includes(dir[index])){
      value = 0;
    }
    else if (index == 0 && loc < xSize){
      value = 0; 
    }
    else if (index == 3 && loc%ySize == 0){
      value = 0; 
    }
    else if (index == 2 && loc%ySize == 1){
      value = 0; 
    }
    else if (index == 0 && loc > mapSize-xSize){
      value = 0;    
    }
    moveValues.push(value);
  }
}

function direction(index){
  const basicdir = ['up','down','left','right'];
  return(
    basicdir[index]
  )
}

function moves(loc){
  var down = loc + xSize;
  var up = loc - xSize;
  var left = loc - 1;
  var right = loc + 1;
  dir.unshift(up,down,left,right);
}

function Mapinit(){

  const [newMap, setnewMap]=useState([]);

  var blockobjectcount = 5;

  for (let index = 0; index < blockobjectcount; index++) {
    var randomnum = Math.floor(Math.random() * mapSize) + 1
    if (randomnum == 1){
      randomnum = randomnum + 1;
    }
    else if (randomnum == mapSize){
      randomnum = randomnum - 1;
    }
    blocks.push(randomnum);   
  }

  for (let index = 0; index < mapSize; index++) {
    var blocknum = index+1;
    if(blocks.includes(blocknum)){
      setnewMap(newMap=>[...newMap,{numv:index+1, func:'block'}])
    }
    else{
      setnewMap(newMap=>[...newMap,{numv:index+1, func:'block'}])
    }
  }

  return(newMap);
}


function drawmap(item){
   var blockstyle;

  if (item.func == 'block'){
    blockstyle = styles.mapblock;
  }
  else if (item.func == 'worm'){
    blockstyle = styles.mapworm;
  }
  else{
    blockstyle = styles.mapfree;
  }
  return(blockstyle);
}



const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  mapfree: {
    justifyContent: 'center',
    flex:1,
    alignItems: 'center',
    height: 50,
    width: 50,
    borderWidth: 2,
    borderColor: 'red',
    backgroundColor: 'white'
  },

  mapblock: {
    justifyContent: 'center',
    flex:1,
    alignItems: 'center',
    height: 50,
    width: 50,
    borderWidth: 2,
    borderColor: 'red',
    backgroundColor: 'red'
  },

  mapworm: {
    justifyContent: 'center',
    flex:1,
    alignItems: 'center',
    height: 50,
    width: 50,
    borderWidth: 2,
    borderColor: 'red',
    backgroundColor: 'blue'
  },

  startbutton: {
    justifyContent: 'center',
    alignItems: 'center',
    height: 50,
    width: 100,
    borderWidth: 2,
    borderColor: 'red',
    backgroundColor: 'blue'
  },

  GridViewInsideTextItemStyle: {

   color: '#fff',
   fontSize: 18,
   color: "black",
   justifyContent: 'center',

 },
});
