import { StatusBar } from 'expo-status-bar';

import React ,{ useState, useEffect }from 'react';

import Component from 'react';

import { DrawerLayoutAndroidComponent, StyleSheet, Text, View, FlatList, Button, Alert } from 'react-native';

import styles from './styles/Gridstyles';

  const map=[];

  var OpenSet=[];
  var ClosedSet=[];

  var xSize = 20;
  var ySize = 20;
  var mapSize = xSize*ySize;

  var start = [0,0];
  var end = [xSize-1,ySize-1];

  var last;

  var UltimateCurrent = [];

  var goal = false;


var testi;

export default class app extends React.Component{

  constructor(props) {
    super(props);
    this.state={movecount:0};
    this.handleUpdate = this.handleUpdate.bind(this);
    this.playagain = this.playagain.bind(this);

    Mapinit();
    UltimateCurrent.push(map[0]);   
  }

  handleUpdate = () =>{
    aStar(UltimateCurrent);
    trail();
    this.setState({movecount :this.state.movecount + 1})    

    if (goal == true){
      map.splice(0,map.length);
      Mapinit();
      UltimateCurrent.push(map[0]);
      this.setState({movecount :this.state.movecount - this.state.movecount}) 
    }
  }

  playagain = () =>{
    map.splice(0,map.length);
    Mapinit();
    UltimateCurrent.push(map[0]);
    this.setState({movecount :this.state.movecount - this.state.movecount})    
}



  render(){
    return(
    <View style={styles.container}>
      <Text>{this.state.movecount}</Text>
      <Button title="start" onPress={this.handleUpdate} style={styles.startbutton}></Button>
      <Button title="Again" onPress={this.playagain} style={styles.startbutton}></Button>
      <FlatList
        data={map}
        extraData={this.state.movecount}
        renderItem={({item}) => <View style={drawmap(item)}>
          <Text style={styles.GridViewInsideTextItemStyle}>{item.fscore}</Text>            
        </View>}
        numColumns={xSize}
      />
    </View>
    );
  } 
}

function aStar(current){
do{
  goal = false;
  var gscore = 0;

  for (let paths = 0; paths < current.length; paths++) {

    for (let index = 0; index < 4; index++) {
      current[paths].Neighbours = Neighbours(current[paths].x, current[paths].y, current[paths].ID);
    }

    for (let index = 0; index < current[paths].Neighbours.length; index++) {
      gscore = current[paths].gscore + 1;

      current[paths].fscore = Distance(current[paths].Location)+gscore;
      current[paths].Neighbours[index].fscore = Distance(current[paths].Neighbours[index].Location)+gscore;

      map[current[paths].Neighbours[index].ID].fscore = current[paths].Neighbours[index].fscore;
      console.log(map[current[paths].Neighbours[index].ID].fscore+"Tämä voin olla");
      console.log(current[paths].fscore+"Tämä olen");

      if(map[current[paths].Neighbours[index].ID].fscore<current[paths].fscore){
        OpenSet.push(map[current[paths].Neighbours[index].ID]);
        console.log(map[current[paths].Neighbours[index].ID].fscore+"Tämän tallensin");
        map[current[paths].Neighbours[index].ID].cameFrom = current[paths].ID;
      }
    }
  }

  for (let index = 0; index < OpenSet.length; index++) {
    console.log(OpenSet[index].Location);
  }

  var min = null;
  var id = null;
  var Delete = null;

  var min = xSize*ySize-1;
  for (let index = 0; index < OpenSet.length; index++) {
    var id = null;
    if (min > OpenSet[index].fscore)  {
      min = OpenSet[index].fscore;
      id = OpenSet[index].ID;
      Delete = index;
    }
  }


  for (let index = 0; index < current.length; index++) {
    if(JSON.stringify(current[index].Location)==JSON.stringify(end)){
      goal = true;
    }  
  }

  last = current[0].Location;
  if (OpenSet.length==0){
     goal = true;
  }


  var cf = current[0].fscore;
  current.splice(0,current.length);

  for (let index = 0; index < OpenSet.length; index++) {
    if(OpenSet[index].fscore<cf){
    current.push(map[OpenSet[index].ID]);
    }
  }

  OpenSet.splice(0,OpenSet.length);

}while(goal!=true)//(JSON.stringify(current.Location)!=JSON.stringify(end))
}

function trail(){

  var trailPath = null;
  var past = true;

  map[0].trail = true;

  do{
    if(trailPath == null){
      for (let index = 0; index < map.length; index++) {
        if(JSON.stringify(map[index].Location) == JSON.stringify(last)){
          trailPath = map[index];
          trailPath.trail = true;
        }     
      }
    }

    if (trailPath.cameFrom==0){
      past = false
    }
    else{
      trailPath = map[trailPath.cameFrom];
      trailPath.trail = true;
    }

  }while(past != false)
}

function DoesInclude(id){
  var already = false;
  for (let index = 0; index < OpenSet.length; index++) {
    if(OpenSet[index].ID == id){
      already = true;
      console.log("tarkistetaan on duplicatee");
      console.log(OpenSet[index].ID);
      console.log(id);
    }
  }
  return already;
}

function cell(nx,ny,random,idnum){

  let ID = idnum;
  let cameFrom = 0;
  let x = nx;
  let y = ny;
  let loc = [x,y];
  let fscore = 0;
  let gscore = 0;
  let wall = wallorNot(random, loc);
  var blocks = [];
  var trail = false;

  // jatka tästä
  return {'x':x,'y':y,'wall':wall, 'Neighbours':blocks, 'Location':loc , 'fscore':fscore,'gscore':gscore ,'ID':ID, 'cameFrom':cameFrom,'trail':trail};
}

function Neighbours(x, y, id){

  var NL = [];

  if(y > 0){ // voiko mennä ylös

    if (map[id-(xSize)].wall != true){
      NL.push(map[id-(xSize)]);
    }
  }
  if(y < ySize-1){ // voiko mennä alas

    if (map[id+(xSize)].wall != true){
      NL.push(map[id+(xSize)]);
    }
  }
  if(x > 0){ // voiko mennä vasemmalle

    if (map[id-1].wall != true){
      NL.push(map[id-1]);
    }
  }
  if(x < xSize-1){ // voiko mennä oikealle

    if (map[id+1].wall != true){
      NL.push(map[id+1]);
    }
  }

  for (let x = 0; x < OpenSet.length; x++) {
    for (let y = 0; y < NL.length; y++) {
      if (OpenSet[x].ID == NL[y].ID){
        NL.splice(y,1);
      }

    }
  }

  return NL;
}

function wallorNot(random,loc){
  if(JSON.stringify(loc)==JSON.stringify(start) || JSON.stringify(loc) == JSON.stringify(end)){
    return false;
  }
  else{
    if(random>0.8){
      return true;
    }
    else{
      return false;
    }
  }
}

function Mapinit(){
  var num = 0;
  for (let y = 0; y < ySize; y++) {
    for (let x = 0; x < xSize; x++) {
      var randomnum = Math.random();
      map.push(cell(x,y,randomnum,num));
      console.log(randomnum);
      num++
     }
  }
}

function drawmap(item){
  var blockstyle;
  if (item.trail == true){
    blockstyle = styles.Trail;
  }
  if (!item.wall){
    if (item.trail == true){
      blockstyle = styles.Trail;
    }
    else{
    blockstyle = styles.mapfree;
    }
  }
  else{
    blockstyle = styles.mapblock;
  }
  return(blockstyle);
}

function Distance(location){
  return Math.abs(location[0]-end[0])+Math.abs(location[1]-end[1]); // toimii vain jos aloitus on vasemmassa yläkulmassa
}
