import { StatusBar } from 'expo-status-bar';

import React ,{ useState, useEffect }from 'react';

import Component from 'react';

import { DrawerLayoutAndroidComponent, StyleSheet, Text, View, FlatList, Button } from 'react-native';

import styles from './styles/Gridstyles';

  const map=[];

  var OpenSet=[];
  var ClosedSet=[];

  var xSize = 5;
  var ySize = 5;
  var mapSize = xSize*ySize;

  var start = [0,0];
  var end = [xSize-1,ySize-1];



  export default class app extends React.Component{

    constructor(props) {
      super(props);
      this.state={movecount:1};
      this.handleUpdate = this.handleUpdate.bind(this);
      Mapinit();   
      console.log(map); 
      console.log(start);
      console.log(end);
    }

    handleUpdate() {

      // var current = start
      // var end = false;

      // var gscore = Distance(map[0].Location);

      // while(OpenSet != null&&end != true){
      //   if (current == end){
      //     end = true;
      //   }
      // }
    }

  render(){
    return(
    <View style={styles.container}>
      <Text>{this.state.movecount}</Text>
      <Button title="start" onPress={this.handleUpdate()} style={styles.startbutton}></Button>
      <FlatList
        data={map}
        extraData={this.state}
        renderItem={({item}) => <View style={drawmap(item)}>
          <Text style={styles.GridViewInsideTextItemStyle}>{item.numv}</Text>            
        </View>}
        numColumns={xSize}
      />
    </View>
    );
  }

}

function cell(nx,ny,random){

  let x = nx;
  let y = ny;
  let loc = [x,y];
  let wall = wallorNot(random, loc);
  var blocks = Neighbours(x,y);
  // jatka tästä
  return {'x':x,'y':y,'wall':wall, 'Neighbours':blocks, 'Location':loc};
}

function Neighbours(x,y){

  var NL = [];

  if(x > 0){ // voiko mennä ylös
    NL.push([x-1,y]);
  }
  if(x < xSize-1){ // voiko mennä alas
    NL.push([x+1,y]);
  }
  if(y > 0){ // voiko mennä vasemmalle
    NL.push([x,y-1]);
  }
  if(x < ySize-1){ // voiko mennä oikealle
    NL.push([x,y+1]);
  }

  return NL;
}

function wallorNot(random,loc){
  if(JSON.stringify(loc)==JSON.stringify(start) || JSON.stringify(loc) == JSON.stringify(end)){
    return false;
  }
  else{
    if(random>0.8){
      return true;
    }
    else{
      return false;
    }
  }
}

function Mapinit(){
  for (let y = 0; y < ySize; y++) {
    for (let x = 0; x < xSize; x++) {
      var randomnum = Math.random();
      map.push(cell(x,y,randomnum));
      console.log(randomnum);
     }
  }
}

function drawmap(item){
  var blockstyle;

  if (!item.wall){
    if (OpenSet.includes(item.loc)){
      blockstyle = styles.OpenSet;
    }
    if (ClosedSet.includes(item.loc)){
      blockstyle = styles.ClosedSet;
    }
    else{
    blockstyle = styles.mapfree;
    }
  }
  else{
    blockstyle = styles.mapblock;
  }
  return(blockstyle);
}

function Distance(location){
  return location[0]+location[1]; // toimii vain jos aloitus on vasemmassa yläkulmassa
}
