import { StatusBar } from 'expo-status-bar';

import React ,{ useState, useEffect }from 'react';

import Component from 'react';

import { DrawerLayoutAndroidComponent, StyleSheet, Text, View, FlatList, Button } from 'react-native';

import styles from './styles/Gridstyles';

  const map=[];

  var OpenSet=[];
  var ClosedSet=[];

  var xSize = 5;
  var ySize = 5;
  var mapSize = xSize*ySize;

  var start = [0,0];
  var end = [xSize-1,ySize-1];

  var UltimateCurrent;


  export default class app extends React.Component{

    constructor(props) {
      super(props);
      this.state={movecount:1};
      this.handleUpdate = this.handleUpdate.bind(this);
      Mapinit();
      UltimateCurrent = map[0];   
      console.log(map); 
      console.log(start);
      console.log(end);
    }

  render(){
    return(
    <View style={styles.container}>
      <Text>{this.state.movecount}</Text>
      <Button title="start" onPress={this.handleUpdate()} style={styles.startbutton}></Button>
      <FlatList
        data={map}
        extraData={this.state}
        renderItem={({item}) => <View style={drawmap(item)}>
          <Text style={styles.GridViewInsideTextItemStyle}>{item.fscore}</Text>            
        </View>}
        numColumns={xSize}
      />
    </View>
    );
  }

  handleUpdate() {
    var goal = false;
    do{
      aStar(UltimateCurrent);
      goal = true;
      this.state.movecount = this.state.movecount + 1;
    }while(this.state.movecount < 25) //JSON.stringify(UltimateCurrent.Location)==JSON.stringify(end)
  }

}

function aStar(current){

  var goal = false;
  var gscore = 0;

  for (let index = 0; index < 4; index++) {
    current.Neighbours = Neighbours(current.x, current.y, current.ID);
  }

  for (let index = 0; index < current.Neighbours.length; index++) {
    gscore = current.gscore + 1;
    current.fscore = Distance(current.Location)+gscore;
    current.Neighbours[index].fscore = Distance(current.Neighbours[index].Location)+gscore;
    map[current.Neighbours[index].ID].fscore = current.Neighbours[index].fscore;
    if(map[current.Neighbours[index].ID].fscore<current.fscore){
      OpenSet.push(map[current.Neighbours[index].ID]);
    }
  }

  var min = null;
  var id = null;
  var Delete = null;

  console.log(OpenSet);

  for (let index = 0; index < OpenSet.length; index++) {
    var min = null;
    var id = null;
    if (min == null){
      min = OpenSet[index].fscore;
      id = OpenSet[index].ID;
      Delete = index;
    }
    else if (min > OpenSet[index].fscore)  {
      min = OpenSet[index].fscore;
      id = OpenSet[index].ID;
      Delete = index;
    }
  }

  UltimateCurrent = map[id];
  OpenSet.splice(Delete,1);

}

function trail(){

}

function cell(nx,ny,random, idnum){

  let ID = idnum
  let x = nx;
  let y = ny;
  let loc = [x,y];
  let fscore = 0;
  let gscore = 0;
  let wall = wallorNot(random, loc);
  var blocks = [];

  // jatka tästä
  return {'x':x,'y':y,'wall':wall, 'Neighbours':blocks, 'Location':loc , 'fscore':fscore,'gscore':gscore ,'ID':ID};
}

function Neighbours(x, y, id){

  var NL = [];

  if(y > 0){ // voiko mennä ylös

    if (map[id-(xSize)].wall != true){
      NL.push(map[id-(xSize)]);
    }
  }
  if(y < ySize-1){ // voiko mennä alas

    if (map[id+(xSize)].wall != true){
      NL.push(map[id+(xSize)]);
    }
  }
  if(x > 0){ // voiko mennä vasemmalle

    if (map[id-1].wall != true){
      NL.push(map[id-1]);
    }
  }
  if(x < xSize-1){ // voiko mennä oikealle

    if (map[id+1].wall != true){
      NL.push(map[id+1]);
    }
  }

  return NL;
}

function wallorNot(random,loc){
  if(JSON.stringify(loc)==JSON.stringify(start) || JSON.stringify(loc) == JSON.stringify(end)){
    return false;
  }
  else{
    if(random>0.8){
      return true;
    }
    else{
      return false;
    }
  }
}

function Mapinit(){
  var num = 0;
  for (let y = 0; y < ySize; y++) {
    for (let x = 0; x < xSize; x++) {
      var randomnum = Math.random();
      map.push(cell(x,y,randomnum,num));
      console.log(randomnum);
      num++
     }
  }
}

function drawmap(item){
  var blockstyle;

  if (!item.wall){
    if (OpenSet.includes(item.loc)){
      blockstyle = styles.OpenSet;
    }
    if (ClosedSet.includes(item.loc)){
      blockstyle = styles.ClosedSet;
    }
    else{
    blockstyle = styles.mapfree;
    }
  }
  else{
    blockstyle = styles.mapblock;
  }
  return(blockstyle);
}

function Distance(location){
  return Math.abs(location[0]-end[0])+Math.abs(location[1]-end[1]); // toimii vain jos aloitus on vasemmassa yläkulmassa
}
