import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { DrawerLayoutAndroidComponent, StyleSheet, Text, View, FlatList } from 'react-native';

  const blocks=[];
  const newMap=[];
  const calcmap=[];


export default function App() {

  Mapinit();

  return (
    <View style={styles.container}>
      <Text>LOL tää toimii</Text>
      <StatusBar style="auto" />
      <FlatList
        data={newMap}
        renderItem={({item}) => <View style={drawmap(item)}>
          <Text style={styles.GridViewInsideTextItemStyle}></Text>            
        </View>}
        numColumns={5}
      />
    </View>
  );
}

function Mapinit(){
  var mapSize = 25;
  var blockobjectcount = 5;

  for (let index = 0; index < blockobjectcount; index++) {
    var randomnum = Math.floor(Math.random() * mapSize) + 1
    if (randomnum == 1){
      randomnum = randomnum + 1;
    }
    else if (randomnum == mapSize){
      randomnum = randomnum - 1;
    }
    blocks.push(randomnum);   
  }

  for (let index = 0; index < mapSize; index++) {
    if(blocks.includes(index+1)){
      newMap.push({numv:index+1, func:'block'})
    }
    else{
      newMap.push({numv:index+1, func:'free'})
    }
  }

  for (let index = 0; index < mapSize/5; index++) {
      calcmap.push(listwithzeros(mapSize/5))
  }

}

function listwithzeros(size){
  const element = [];
  for (let index = 0; index < size; index++) {
    element.push(0);
  }

  return(element);
}

function drawmap(item){
   var blockstyle;

  if (item.func == 'block'){
    blockstyle = styles.mapblock;
  }
  else if (item.func == 'worm'){
    blockstyle = styles.mapworm;
  }
  else{
    blockstyle = styles.mapfree;
  }


  return(blockstyle);
}



const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  mapfree: {
    justifyContent: 'center',
    flex:1,
    alignItems: 'center',
    height: 50,
    width: 50,
    borderWidth: 2,
    borderColor: 'red',
    backgroundColor: 'white'
  },

  mapblock: {
    justifyContent: 'center',
    flex:1,
    alignItems: 'center',
    height: 50,
    width: 50,
    borderWidth: 2,
    borderColor: 'red',
    backgroundColor: 'red'
  },

  mapworm: {
    justifyContent: 'center',
    flex:1,
    alignItems: 'center',
    height: 50,
    width: 50,
    borderWidth: 2,
    borderColor: 'red',
    backgroundColor: 'blue'
  },

  GridViewInsideTextItemStyle: {

   color: '#fff',
   fontSize: 18,
   justifyContent: 'center',

 },
});
