import { StatusBar } from 'expo-status-bar';

import React ,{ useState, useEffect }from 'react';

import Component from 'react';

import { DrawerLayoutAndroidComponent, StyleSheet, Text, View, FlatList, Button } from 'react-native';

  const blocks=[];
  const moveValues=[];
  const map=[];
  const beenThere=[];
  const futureValues=[];

  var xSize = 5;
  var ySize = 5;
  var mapSize = xSize*ySize;
  const dir = [];
  var loc = 0;


  export default class app extends React.Component{

    constructor(props) {
      super(props);
      this.state={movecount:1};
      this.handleUpdate = this.handleUpdate.bind(this);
      Mapinit();    
    }

    handleUpdate() {
      var pressed = true;
      do{

        map[loc] = {numv:loc + 1, func:'worm'};
        beenThere.push(loc);

        bestmove(loc)
        LegitMove(loc);
        var movedir = Math.max.apply(null, moveValues);
        var dirIndex = moveValues.indexOf(movedir);

        console.log(moveValues);
        console.log(loc);
        console.log(movedir);

        switch (dirIndex) {
          case 0:
            loc = loc - 5;
            break;
          case 1:
            loc = loc + 5;
            break;
          case 2:
            loc = loc - 1;
            break;
          case 3:
            loc = loc + 1;
            break;
        }

        pressed = false;
        this.setState({movecount: this.state.movecount + 1});
    }while(pressed == true);
  }

  render(){
    return(
    <View style={styles.container}>
      <Text>{this.state.movecount}</Text>
      <Button title="start" onPress={this.handleUpdate} style={styles.startbutton}></Button>
      <FlatList
        data={map}
        extraData={this.state}
        renderItem={({item}) => <View style={drawmap(item)}>
          <Text style={styles.GridViewInsideTextItemStyle}>{item.numv}</Text>            
        </View>}
        numColumns={5}
      />
    </View>
    );
  }

}

function DistanceToEnd(loc){
  var distance;
  if (loc<0){
    distance=0;
  }
  else if (loc < 6){
    distance = 5 - loc + 4
    distance = 24 - distance
  }
  else if (loc < 11){
    distance = 10 - loc + 3
    distance = 24 - distance
  }
  else if (loc < 16){
    distance = 15 - loc + 2
    distance = 24 - distance
  }
  else if (loc < 21){
    distance = 20 - loc + 1
    distance = 24 - distance
  }
  else if (loc < 25){
    distance = 24 - loc + 1
    distance = 24 - distance
  }
  else if (loc > 24){
    distance = 0;
  }
  return(
    distance
  )
}
function bestmove(loc){

  moveValues.splice(0,4);
  futureValues.splice(0,4);

  moves(loc)
  countmoves(loc);

  for (let index = 0; index < moveValues.length; index++) { 
    if (moveValues[index]>0){
      moveValues[index] = moveValues[index] + futureMoves(dir[index])
    }

  }
  console.log(futureValues);
}


function futureMoves(loc){
  var futVal = 0;

  var up = DistanceToEnd(dir[0]);
  var down = DistanceToEnd(dir[1]);
  var left = DistanceToEnd(dir[2]);
  var right = DistanceToEnd(dir[3]);

  if (0 || 5 || 10 || 15 || 20){
    left = 0;
  }
  else if(4 || 9 || 14 || 19 || 24){
    right = 0;
  }
  else if(loc>19 && loc<25){
    down = 0;
  }

  futureValues.push(up,down,left,right);

  for (let index = 0; index < dir.length; index++) {
    if(blocks.includes(dir[index] + 1)||beenThere.includes(dir[index])){
      futureValues[index] = 0;
    }
  }

  for (let index = 0; index < dir.length; index++) {
      futVal = futVal + futureValues[index]
  }

  return (futVal);

}

function countmoves(loc){

  var up = DistanceToEnd(dir[0]);
  var down = DistanceToEnd(dir[1]);
  var left = DistanceToEnd(dir[2]);
  var right = DistanceToEnd(dir[3]);

  moveValues.push(up,down,left,right);

}

function LegitMove(loc){

  if (loc == 0 || loc ==5 || loc ==10 || loc ==15 || loc ==20){
    moveValues[2] = 0;
  }
  else if(loc ==4 || loc ==9 || loc ==14 || loc ==19 || loc ==24){
    moveValues[3] = 0;
  }
  else if(loc>19 && loc<25){
    moveValues[1] = 0;
  }

  for (let index = 0; index < dir.length; index++) {
    if(blocks.includes(dir[index] + 1)||beenThere.includes(dir[index])){
      moveValues[index] = 0;
    }
  }
}

function moves(loc){

  dir.splice(0,4);
  var down = loc + ySize;
  var up = loc - ySize;
  var left = loc - 1;
  var right = loc + 1;
  dir.push(up,down,left,right);
  console.log(dir);
}

function Mapinit(){

  var blockobjectcount = 5;

  for (let index = 0; index < blockobjectcount; index++) {
    var randomnum = Math.floor(Math.random() * mapSize) + 1
    if (randomnum == 1){
      randomnum = randomnum + 1;
    }
    else if (randomnum == mapSize){
      randomnum = randomnum - 1;
    }
    blocks.push(randomnum);   
  }

  for (let index = 0; index < mapSize; index++) {
    var blocknum = index+1;
    if(blocks.includes(blocknum)){
      map.push({numv:index+1, func:'block'});
    }
    else{
      map.push({numv:index+1, func:'free'});
    }
  }
}


function drawmap(item){
   var blockstyle;

  if (item.func == 'block'){
    blockstyle = styles.mapblock;
  }
  else if (item.func == 'worm'){
    blockstyle = styles.mapworm;
  }
  else{
    blockstyle = styles.mapfree;
  }
  return(blockstyle);
}



const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  mapfree: {
    justifyContent: 'center',
    flex:1,
    alignItems: 'center',
    height: 50,
    width: 50,
    borderWidth: 2,
    borderColor: 'red',
    backgroundColor: 'white'
  },

  mapblock: {
    justifyContent: 'center',
    flex:1,
    alignItems: 'center',
    height: 50,
    width: 50,
    borderWidth: 2,
    borderColor: 'red',
    backgroundColor: 'red'
  },

  mapworm: {
    justifyContent: 'center',
    flex:1,
    alignItems: 'center',
    height: 50,
    width: 50,
    borderWidth: 2,
    borderColor: 'red',
    backgroundColor: 'blue'
  },

  startbutton: {
    justifyContent: 'center',
    alignItems: 'center',
    height: 50,
    width: 100,
    borderWidth: 2,
    borderColor: 'red',
    backgroundColor: 'blue'
  },

  GridViewInsideTextItemStyle: {

   color: '#fff',
   fontSize: 18,
   color: "black",
   justifyContent: 'center',

 },
});
